    private void money() {
        double value = 0;
        value = Double.parseDouble(valueField.getText());
        value = Math.round(value/3 + 1000);                                                                 //calculates the value then labels it
        moneyLabel.setText("You should be spending " +Double.toString(value) + "$ MAX on this round.");
    }
