    private void calcButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calcButtonActionPerformed
       if (Double.parseDouble(valueField.getText()) > 2000 && Double.parseDouble(valueField.getText()) < 16000 ) {        //makes sure the money is in the range
            money();
       }
       else {
           moneyLabel.setText("MONEY HAS TO BE ABOVE 2000 AND BELOW 16000");    //tells user input is invalid
       }        
    }//GEN-LAST:event_calcButtonActionPerformed


